mapper.select:
SELECT
    
FROM
waybill_tab w

    
        
            
                
                    AND w.groupid = #{groupId}
                
                
                    
                         AND w.userid = #{userId} AND w.groupid <= 0 
                        
                    
                
            
        
        
            AND (w.userid = #{userId} OR (w.groupid IN (SELECT groupid FROM group_members_tab WHERE userid = #{userId})))
        
    
    
        AND (
        (LOCATE(#{likeString}, w.delivery_number) > 0) OR
        (LOCATE(#{likeString}, w.barcode) > 0) OR
        (LOCATE(#{likeString}, w.receiver_name) > 0) OR
        (LOCATE(#{likeString}, w.receive_address) > 0) OR
        (LOCATE(#{likeString}, w.order_summary) > 0)
        )
    
    
         AND w.contact_phone = #{mobilphone} 
    
    
         AND w.createtime >= #{firstTime} 
    
    
         AND w.createtime <= #{secondTime} 
    
    
     AND w.delivery_time >= #{deliveryTimeStart} 
    
    
         AND w.delivery_time <= #{deliveryTimeEnd} 
    
    
         AND LOCATE(#{startStation}, w.start_station) > 0 
    
    
         AND  LOCATE(#{endStation}, w.end_station) > 0 
    
    
         AND w.delay = #{delay} 
    
    
        AND w.waybill_status IN
        
            #{fettle}
        
    
    
        AND w.receipt_verify_status IN
        
            #{fettle}
        
    
    
        
            AND (w.receipt_count = 0 OR w.receipt_count > w.receipt_verify_count OR w.receipt_unqualify_count > 0)
        
        
            AND w.receipt_count = 0
        
        
            AND (w.receipt_count > 0 AND w.receipt_verify_count = 0)
        
        
            AND (w.receipt_verify_count >= 1 AND w.receipt_count > w.receipt_verify_count)
        
        
            AND (w.receipt_count > 0 AND w.receipt_verify_count >= w.receipt_count AND w.receipt_unqualify_count
            > 0)
        
        
            AND w.receipt_count > w.receipt_verify_count
        
    

ORDER BY w.createtime DESC, w.waybill_status ASC

        SELECT
        	
        FROM
            waybill_tab w
	    
	      
	           AND DATE_FORMAT(w.createtime,'%Y-%m-%d') >= #{deliverStartTime} 
	      
	      
                 AND DATE_FORMAT(w.createtime,'%Y-%m-%d') <= #{deliverEndTime}

SELECT DISTINCT w.id AS _id, FROM waybill_tab w
AND w.groupid = #{groupId}
AND (w.userid = #{userId} AND w.groupid <= 0)
AND (w.userid = #{userId} OR (w.groupid IN (SELECT groupid FROM group_members_tab WHERE userid = #{userId})))
AND ( (LOCATE(#{likeString}, w.delivery_number) > 0) OR (LOCATE(#{likeString}, w.barcode) > 0) OR (LOCATE(#{likeString}, w.receiver_name) > 0) OR (LOCATE(#{likeString}, w.receive_address) > 0) OR (LOCATE(#{likeString}, w.order_summary) > 0) )
AND w.contact_phone = #{mobilphone}
AND w.createtime >= #{firstTime}
AND w.createtime <= #{secondTime}
AND LOCATE(#{startStation}, w.start_station) > 0
AND LOCATE(#{endStation}, w.end_station) > 0
AND w.delay = #{delay}
AND w.waybill_status IN
#{fettle}
AND w.receipt_verify_status IN
#{fettle}
AND (w.receipt_count = 0 OR w.receipt_count > w.receipt_verify_count OR w.receipt_unqualify_count > 0)
AND w.receipt_count = 0
AND (w.receipt_count > 0 AND w.receipt_verify_count = 0)
AND (w.receipt_verify_count >= 1 AND w.receipt_count > w.receipt_verify_count)
AND (w.receipt_count > 0 AND w.receipt_verify_count >= w.receipt_count AND w.receipt_unqualify_count > 0)
AND w.receipt_count > w.receipt_verify_count
ORDER BY w.createtime DESC, w.waybill_status ASC

SELECT

FROM
waybill_tab w

    
        
            AND w.groupid = #{groupId}
        
        
             AND w.userid = #{userId}  and w.groupid <= 0 
        
    
    
        AND (
        (LOCATE(#{likeString}, w.delivery_number) > 0) OR
        (LOCATE(#{likeString}, w.barcode) > 0) OR
        (LOCATE(#{likeString}, w.receiver_name) > 0) OR
        (LOCATE(#{likeString}, w.receive_address) > 0) OR
        (LOCATE(#{likeString}, w.order_summary) > 0)
        )
    
    
         AND w.contact_phone = #{mobilphone} 
    
    
         AND w.createtime >= #{firstTime} 
    
    
         AND w.createtime <= #{secondTime} 
    
    
         AND w.delay = #{delay} 
    
    
        AND w.waybill_status IN
        
            #{fettle}
        
    
    
         AND w.waybill_status <> 10 
    
    
        AND w.receipt_verify_status IN
        
            #{fettle}
        
    

ORDER BY w.delay, w.createtime DESC

select * from (SELECT a.id, a.barcodeid, a.barcode, a.order_summary, a.address, a.loaction_time, a.receiver_name, a.start_station, a.end_station, a.receive_address, a.createtime as createtime, a.actual_arrival_time, a.arrivaltime, a.delivery_number, a.groupid, a.longitude, a.latitude, a.waybill_status, a.delivery_time, a.shipper_name, a.delay, '' AS uname, '' AS shareid, 1 AS TYPE, (SELECT COUNT(1) from track_tab tr where tr.waybillid = a.id and tr.userid = dws.userid) as position_count FROM waybill_tab a INNER JOIN driver_waybill_status_tab dws ON dws.waybillid = a.id WHERE dws.userid = #{userId,jdbcType=INTEGER} UNION SELECT a.id, a.barcodeid, a.barcode, a.order_summary, a.address, a.loaction_time, a.receiver_name, a.start_station, a.end_station, a.receive_address, a.createtime AS createtime, a.actual_arrival_time, a.arrivaltime, a.delivery_number, a.groupid, a.longitude, a.latitude, a.waybill_status, a.delivery_time, a.shipper_name, a.delay, u.uname, c.shareid, 2 AS TYPE, a.position_count FROM waybill_tab a INNER JOIN share_user_jurisdiction_tab c ON a.id=c.waybillid LEFT JOIN user_tab u ON c.shareid = u.id WHERE c.acceptid=#{userId,jdbcType=INTEGER} AND c.waybillid NOT IN (SELECT s.waybillid FROM driver_waybill_status_tab s WHERE s.userid=#{userId,jdbcType=INTEGER} ) GROUP BY a.id ) t where 1=1
and (t.delivery_number like concat('%', #{likeString,jdbcType=VARCHAR}, '%') or t.order_summary like concat('%', #{likeString,jdbcType=VARCHAR}, '%') or t.receiver_name like concat('%', #{likeString,jdbcType=VARCHAR}, '%') or t.receive_address like concat('%', #{likeString,jdbcType=VARCHAR}, '%'))
AND t.createtime >= #{firstTime}
AND t.createtime <= #{secondTime}
AND t.waybill_status IN
#{fettle}
AND t.delay = #{delay}
order by t.createtime desc

